import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { HttpClient } from '@angular/common/http';
import { ASSISTANT_API_CONFIG } from '../../app.config';
import { AssistantService } from './assistant.service';
import { Message } from '../../interfaces/assistant.model';
import { PromptService } from './prompt.service';

@Injectable({
  providedIn: 'root',
})
export class MessageService {
  private messagesSubject = new BehaviorSubject<Message[]>([]); // Observable for messages
  messages$ = this.messagesSubject.asObservable(); // Expose messages as observable

  private isLoadingSubject = new BehaviorSubject<boolean>(false); // To track loading state
  isLoading$ = this.isLoadingSubject.asObservable(); // Observable for loading state

  constructor(
    private http: HttpClient,
    private assistantService: AssistantService,
    private promptService: PromptService
  ) {}

  sendPrompt(prompt: string, extraInstruction?: string): void {
    // Log the request
    const requestMessage: Message = {
      id: this.generateMessageId(),
      type: 'request',
      content: prompt,
      timestamp: new Date().toISOString(),
    };
    this.addMessage(requestMessage); // Add request to message history

    this.isLoadingSubject.next(true); // Set loading state to true

    // Using Observer pattern instead of separate callbacks
    this.promptService.prompt(prompt, extraInstruction).subscribe({
      next: (response) => {
        const responseMessage: Message = {
          id: this.generateMessageId(),
          type: 'response',
          content: response?.data || 'No data',
          timestamp: new Date().toISOString(),
        };
        this.addMessage(responseMessage); // Add response to message history
        this.isLoadingSubject.next(false); // Set loading state to false
      },
      error: (error) => {
        const errorMessage: Message = {
          id: this.generateMessageId(),
          type: 'response',
          content: 'Failed to process prompt.',
          timestamp: new Date().toISOString(),
        };
        this.addMessage(errorMessage); // Add error response
        this.isLoadingSubject.next(false); // Set loading state to false
      },
      complete: () => {
        // Optionally handle completion (if necessary)
        console.log('Prompt processing completed');
      },
    });
  }

  // Add a message (request/response) to history
  private addMessage(message: Message): void {
    const currentMessages = this.messagesSubject.value;
    this.messagesSubject.next([...currentMessages, message]);
  }

  // Utility to generate unique message ID
  private generateMessageId(): string {
    return Math.random().toString(36).substr(2, 9); // Simple ID generator
  }
}

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { map, Observable, of } from 'rxjs';
import { ASSISTANT_API_CONFIG } from '../../app.config';
import { AssistantService } from './assistant.service';
import { Message } from '../../interfaces/assistant.model';

@Injectable({
  providedIn: 'root',
})
export class PromptService {
  private apiUrl =
    ASSISTANT_API_CONFIG.baseUrl + ASSISTANT_API_CONFIG.promptUrl; // Base URL for prompt API

  constructor(
    private http: HttpClient,
    private assistantService: AssistantService
  ) {}

  /**
   * Sends a prompt request to the API.
   * @param id The ID for the prompt request
   * @param prompt The prompt message
   * @param extraInstruction Optional additional instructions
   * @returns Observable with the server's response
   */
  sendPrompt(
    id: string,
    prompt: string,
    extraInstruction?: string
  ): Observable<any> {
    const requestBody = {
      id,
      prompt,
      extraInstruction,
    };

    // Log the request message before sending
    const requestMessage: Message = {
      id: this.generateMessageId(),
      type: 'request',
      content: prompt,
      timestamp: new Date().toISOString(),
    };

    return this.http.post<any>(`${this.apiUrl}`, requestBody);
  }

  // Utility to generate a unique message ID
  private generateMessageId(): string {
    return Math.random().toString(36).substr(2, 9); // Simple ID generator
  }

  prompt(prompt: string, extraInstruction?: string): Observable<any> {
    const selectedAssistant = this.assistantService.getSelectedAssistant();
    if (!selectedAssistant) return of(null);
    return this.sendPrompt(selectedAssistant.id, prompt, extraInstruction);
  }
}

